// Get the actual payload from webhook body
const webhookData = $input.first().json;
const data = webhookData.body || webhookData;

// Validate structure
if (!data.week || !data.posts || !Array.isArray(data.posts)) {
  throw new Error('Invalid payload: missing week or posts array');
}

if (data.posts.length === 0) {
  throw new Error('No posts in payload');
}

const now = new Date();
const errors = [];

for (const post of data.posts) {
  const slot = post.slot || 'unknown';

  if (!post.scheduled_datetime) {
    errors.push(`Post ${slot}: missing scheduled_datetime`);
    continue;
  }
  if (!post.content) {
    errors.push(`Post ${slot}: missing content`);
    continue;
  }

  const scheduledTime = new Date(post.scheduled_datetime);
  if (scheduledTime <= now) {
    errors.push(`Post ${slot}: scheduled time is in the past`);
  }

  if (post.metadata?.word_count > 300) {
    errors.push(`Post ${slot}: exceeds 300 word limit`);
  }

  if (post.hashtags && post.hashtags.length > 3) {
    errors.push(`Post ${slot}: more than 3 hashtags`);
  }
}

if (errors.length > 0) {
  throw new Error('Validation failed:\n' + errors.join('\n'));
}

return [{
  json: {
    ...data,
    validated: true,
    post_count: data.posts.length
  }
}];
